import { Component } from '@angular/core';
import { ChatService } from '../chat.service';

@Component({
  selector: 'app-group-chat',
  templateUrl: './group-chats.component.html',
  styleUrls: ['./group-chats.component.css']
})
export class GroupChatComponent {
  currentGroupChatId!: 0;
  message = '';

  constructor(private chatService: ChatService) { }

  joinGroupChat(): void {
    this.chatService.joinGroupChat(this.currentGroupChatId);
  }

  sendMessage(): void {
    this.chatService.sendGroupMessage(this.currentGroupChatId, this.message);
    this.message = ''; // Clear the message input
  }

  ngOnInit(): void {
    this.chatService.registerGroupMessageHandler((groupChatId, userId, message) => {
      // Handle the received message
      // Update your UI to display the message
      console.log(`Message from group ${groupChatId}: ${message}`);
    });
  }
}
