import { Component, OnInit } from '@angular/core';
import { GroupChatService } from '../group-chats.service';

@Component({
  selector: 'app-group-chats',
  templateUrl: './group-chats.component.html',
  styleUrls: ['./group-chats.component.css']
})
export class GroupChatsComponent implements OnInit {
  groupChats: any[] = [];

  constructor(private groupChatService: GroupChatService) { }

  ngOnInit(): void {
    this.groupChatService.getUserGroupChats().subscribe(
      data => {
        this.groupChats = data;
      },
      error => {
        console.error('Error fetching group chats', error);
      }
    );
  }
}
